#ifndef ALGORITHM_INTERVIEW2_438_FIND_ALL_ANAGRAMS_IN_A_STRING_H
#define ALGORITHM_INTERVIEW2_438_FIND_ALL_ANAGRAMS_IN_A_STRING_H

#include <iostream>
#include <vector>
#include <unordered_map>
#include <unordered_set>
using namespace std;

class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        vector<int> res;
        vector<int> map(256, 0), window(256,0);
        int slen = s.size();
        int plen = p.size();
        if(slen < plen) return res;
        for(auto pi : p){
            map[pi]++;
        }
        for(int i=0; i<plen-1; i++){
            window[ s[i] ]++;
        }
        for(int i=plen-1; i<slen; i++){
            window[ s[i] ]++;
            if(map == window){
                res.push_back(i-plen+1);
            }
            window[ s[i-plen+1] ]--;
        }
        return res;
    }
};

class Test{
public:
    void test(){
//        string s = "";
//        string p = "";
        string s = "aabaa";
        string p = "aa";
        vector<int> res = Solution().findAnagrams(s, p);
        for(auto r : res){
            printf("%d", r);
        }
    }
};

#endif //ALGORITHM_INTERVIEW2_438_FIND_ALL_ANAGRAMS_IN_A_STRING_H
